import React from "react";
import { Carousel } from "react-bootstrap";

import slide1 from "../assets/slider1.jpg";

const titleCarausel = {
  fontWeight: "900",
  fontSize: "500%",
  textShadow: "1px 1px 2px grey, 0 0 25px grey, 0 0 5px grey",
};

function CarauselItem(props) {
  return (
    <Carousel.Item>
      <img className="d-block w-100" src={slide1} alt="Slide" />
      <Carousel.Caption style={{ bottom: "40%", textAlign: "right" }}>
        <h1 style={titleCarausel}>{props.title || "Title here"}</h1>
        <p>{props.subtitle || "Subtitle here"}</p>
      </Carousel.Caption>
    </Carousel.Item>
  );
}

export default CarauselItem;
