import React from "react";
import { Col, Collapse, Container, Row, Button } from "react-bootstrap";

import Card from "react-bootstrap/Card";
function Greeting(props) {
  const isLoggedIn = props.isLoggedIn;
  if (isLoggedIn) {
    return "hai";
  }
  return "wini";
}

function CardProduct(props) {
  return (
    <Col>
      <Card>
        <Card.Img
          variant="top"
          //   src={require("../assets/product_item/p_default.png")}
          src={require("../assets/product_item/" +
            `${props.image || "p_default.png"}`)}
        />
        <Card.Body>
          <Card.Title>{props.title || "Product Title"}</Card.Title>
          <Card.Text style={{ color: "#e39210" }}>
            <b>Rp. {props.price || "NA"}</b>
          </Card.Text>
          <Card.Text>{props.desc || "Product Description"}</Card.Text>
          <Button className="btn-success">Order</Button>
        </Card.Body>

        <p
          style={{
            position: "absolute",
            top: "0",
            left: "0",
            backgroundColor: "orange",
            padding: "5px 20px",
            borderRadius: "3px 0 15px 0",
            color: "white",
          }}
        >
          Disc {{}}%
        </p>
      </Card>
    </Col>
  );
}
export default CardProduct;
