import { React, useState } from "react";
import { Col, Container, Row, Form } from "react-bootstrap";
import Button from "react-bootstrap/Button";

// function handleSubmit() {
//   console.log("wini disini");
// }

function Order() {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [qty, setQty] = useState("");
  const [discount, setDiscount] = useState("");
  const [total, setTotal] = useState("");
  const [paid, setPaid] = useState("");
  const [change, setChange] = useState("");

  const [inputs, setInputs] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    // let umur_sekarang = 2022 - parseInt(inputs.umur);
    // setUmur(umur_sekarang);
    // setName(inputs.nama);
    let total = parseInt(inputs.price) * parseInt(inputs.qty);
    setTotal(total);

    console.log("haiiiii");
  };

  return (
    <div className="my-5" style={{ paddingBottom: "80px" }}>
      <Row>
        <Col className="text-center">
          <h2>Order</h2>
          <p style={{ color: "#e39210", fontStyle: "italic" }}>
            Add your order here.
          </p>
        </Col>
      </Row>

      <Row className="justify-content-center">
        <Col md={8} lg={8}>
          <Container className="py-5">
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3" controlId="nmbrng">
                <Form.Label>Nama Barang</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  placeholder="Nama Barang"
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="harga">
                <Form.Label>Harga (Rp)</Form.Label>
                <Form.Control type="number" name="price" placeholder="Harga" />
              </Form.Group>
              <Form.Group className="mb-3" controlId="qty">
                <Form.Label>Qty</Form.Label>
                <Form.Control type="number" name="qty" placeholder="Quantity" />
              </Form.Group>
              <Form.Group className="mb-3" controlId="discount">
                <Form.Label>Discount (%)</Form.Label>
                <Form.Control
                  type="number"
                  name="discount"
                  placeholder="Discount"
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="total">
                <Form.Label>Total (Rp)</Form.Label>
                <Form.Control
                  type="number"
                  name="total"
                  placeholder="Total"
                  readOnly
                  value={total}
                />
              </Form.Group>

              <hr />

              <Form.Group className="mb-3" controlId="paid">
                <Form.Label>Bayar (Rp)</Form.Label>
                <Form.Control type="number" name="paid" placeholder="paid" />
              </Form.Group>
              <Form.Group className="mb-3" controlId="change">
                <Form.Label>Sisa/Kembalian (Rp)</Form.Label>
                <Form.Control
                  type="number"
                  name="change"
                  placeholder="Rp. "
                  readOnly
                />
              </Form.Group>

              <Button variant="success" type="submit">
                Submit
              </Button>
            </Form>
          </Container>
        </Col>
      </Row>
    </div>
  );
}

export default Order;
